import React from 'react'
import { useDispatch } from 'react-redux'
import { addTocart,removeFromCart } from '../shoppingSlice'
const CartItem = ({name,quantity,price,totalPrice,id}) => {
    const dispatch=useDispatch()
    const incrementItem=()=>{
        dispatch(addTocart({
            name,price,quantity,id
        }))
    }
    const decrementtItem=()=>{
        dispatch(removeFromCart(id))
    }
  return (
    <div>
        <h2>{name}</h2>
        <p>${price}</p>
        <p>X{quantity}</p>
        <button onClick={incrementItem}>+</button>
        <button onClick={decrementtItem}>-</button>
        <h4>totalPrice={totalPrice}</h4>
    </div>
  )
}

export default CartItem
