import React from 'react'
import { useSelector } from 'react-redux'
import CartItem from './CartItem'
const CartItems = () => {
const items=useSelector(state=>state.itemList)
let TotalCost=0;
items.forEach(item=>TotalCost+=item.totalPrice)
return (
    <div>
       <h2>CartItems</h2>
       <ul>
        {items.map((el,id)=>{
            return<li key={el.id}><CartItem id={el.id} name={el.name} price={el.price} quantity={el.quantity} totalPrice={el.totalPrice}/></li>
        })}
       </ul>
       <div>TotalCost={TotalCost}</div>
    </div>
  )
}

export default CartItems
