import {createSlice,configureStore} from '@reduxjs/toolkit'

const shoppingSlice=createSlice({
    name:'shoppingApp',
    initialState:{
       itemList:[],
       totalQuantity:0,
   
    },
    reducers:{
        addTocart:(state,action)=>{
           const newItem=action.payload
           //to chek if item already exist
           const existingItem=state.itemList.find((item)=>item.id===newItem.id)
           if(existingItem){
            existingItem.quantity++;
            existingItem.totalPrice=newItem.price*existingItem.quantity
            
           }else{
            state.itemList.push({
                id:newItem.id,
                price:newItem.price,
                quantity:1,
                totalPrice:newItem.price,
                name:newItem.name

            })
            state.totalQuantity++;
           }
        },
        removeFromCart:(state,action)=>{
           const id=action.payload;
           const existingItem=state.itemList.find(item=>item.id===id)
           if(existingItem===1){
            state.itemList=state.itemList.filter(item=>item.d!==id)
           }else{
            existingItem.quantity--;
            existingItem.totalPrice-=existingItem.price
           }
        },
        
        
    }
})

const store=configureStore({
    reducer:shoppingSlice.reducer
})
export const {addTocart,removeFromCart,setShowCart}=shoppingSlice.actions
export default store
