import React from 'react'
import { useSelector } from 'react-redux'
import { Link } from 'react-router-dom'
const Cart = () => {
    let quantity=useSelector(state=>state.totalQuantity)
  return (
    <div>
   <Link to='/cart'><h3>Cart:{quantity} items</h3></Link> 
    </div>
  )
}

export default Cart
