import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { increment } from '../shoppingSlice'
const CartItems = () => {
const items=useSelector(state=>state.itemList)
const dispatch=useDispatch()
const incrementItem=()=>{
  dispatch(increment())
}
return (
    <div>
       <h2>CartItems</h2>
       <ul>
        {items.map((el,id)=>{
            return<li key={el.id}>{el.name} Price:{el.price}Quantity:{el.quantity} <button onClick={incrementItem}>+</button> <button onClick={decrementItem}>-</button>Total:{el.totalPrice}
            </li>
        })}
       </ul>
    </div>
  )
}

export default CartItems
